import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";

import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH } from "../../shared/util/validators";
import { useForm } from "../../shared/hooks/form-hook";

import "./TaskForm.css";
import Card from "../../shared/components/UIElements/Card";

const UpdateTask = () => {
  const { isLoading, error, sendRequest, clearError} = useHttpClient();
  const [loadedTask, setLoadedTask] = useState();
  const taskId = useParams().taskId;
  const history = useHistory();

  const [formState, inputHandler, setFormData] = useForm({
    creator: {
        value: '',
        isValid: false
    },
    taskDesc: {
        value: '',
        isValid: false
    }
  }, false);

  useEffect(() => {
    const fetchTask = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5000/api/tasks/update/${taskId}`
        );
        setLoadedTask(responseData.task)
        setFormData({
          creator: {
              value: responseData.task.creator,
              isValid: true
          },
          taskDesc: {
              value: responseData.task.taskDesc,
              isValid: true
          }
        }, true);
      } catch (err) {}    
    };
    fetchTask();
  }, [sendRequest, taskId, setFormData]);

  const taskUpdateSubmitHandler = async event => {
    event.preventDefault();
    try{
      await sendRequest(
        `http://localhost:5000/api/tasks/update/${taskId}`,
        'PATCH',
        JSON.stringify({
          creator: formState.inputs.creator.value,
          taskDesc: formState.inputs.taskDesc.value
        }),
        {
            'Content-Type' : 'application/json'
        }
      );
      history.push('/tasks/:taskId');
    } catch (err) {}
  };

  if (isLoading) {
    return (
        <div className="center">
            <LoadingSpinner />
        </div>
    );
  }

  if (!loadedTask && !error) {
    return (
      <div className="center">
        <Card>
          <h2>Could not find the task!</h2>
        </Card>
      </div>
    );
  }

  return (
   <React.Fragment>
       {!isLoading && loadedTask && (
    <form className="task-form" onSubmit={taskUpdateSubmitHandler}>
        <Input 
          id="creator" 
          element="input" 
          type="text" 
          label="Creator" 
          validators={[VALIDATOR_REQUIRE()]}
          errorText="Please Enter a Valid Creator Name"
          onInput= {inputHandler}
          initialValue={formState.inputs.creator.value}
          initialValid={formState.inputs.creator.isValid}
        />
         <Input 
          id="taskDesc" 
          element="textarea"  
          label="Task" 
          validators={[VALIDATOR_MINLENGTH(5)]}
          errorText="Please Enter a Valid Task (At Least 5 Characters)"
          onInput= {inputHandler}
          initialValue={formState.inputs.taskDesc.value}
          initialValid={formState.inputs.taskDesc.isValid}
        />
        <Button type="submit" disabled={!formState.isValid}>UPDATE TASK</Button>
    </form>
    )}
   </React.Fragment>  
  );
};

export default UpdateTask;
