import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import Card from "../../shared/components/UIElements/Card";
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH } from "../../shared/util/validators";
import { useForm } from "../../shared/hooks/form-hook";

import "./TaskForm.css";

const DUMMY_TASKS = [
    {
        taskId : 't1',
        creator : 'Ananya Gupta',
        taskDesc : 'Study JavaScript',
        creatorId: 'c1'
    },
    {
        taskId : 't2',
        creator : 'Dr. Mishra',
        taskDesc : 'Travel whole world'
    }
];

const UpdateTask = () => {
  const [isLoading, setIsLoading] = useState(true);
  const taskId = useParams().taskId;

  const [formState, inputHandler, setFormData] = useForm({
    creator: {
        value: '',
        isValid: false
    },
    task: {
        value: '',
        isValid: false
    }
  }, false);

  const identifiedTask = DUMMY_TASKS.find(t => t.id === taskId);

  useEffect(() => {
    if (identifiedTask) {
      setFormData({
        creator: {
            value: identifiedTask.creator,
            isValid: true
        },
        task: {
            value: identifiedTask.task,
            isValid: true
        }
      }, true);
    }
    setIsLoading(false);
  }, [setFormData, identifiedTask]);

  const taskUpdateSubmitHandler = event => {
    event.preventDefault();
    console.log(formState.inputs);
  };

  if(!identifiedTask) {
    return (
        <div className = "center">
          <Card>
            <h2>Task Not Found</h2>
          </Card>
        </div>  
    );
  }

  if (isLoading) {
    return (
        <div className="center">
            <h2>Loading....</h2>
        </div>
    );
  }

  return (
    <form className="task-form" onSubmit={taskUpdateSubmitHandler}>
        <Input 
          id="creator" 
          element="input" 
          type="text" 
          label="Creator" 
          validators={[VALIDATOR_REQUIRE()]}
          errorText="Please Enter a Valid Creator Name"
          onInput= {inputHandler}
          initialValue={formState.inputs.title.value}
          initialValid={formState.inputs.title.isValid}
        />
         <Input 
          id="task" 
          element="textarea"  
          label="Task" 
          validators={[VALIDATOR_MINLENGTH(5)]}
          errorText="Please Enter a Valid Task (At Least 5 Characters)"
          onInput= {inputHandler}
          initialValue={formState.inputs.description.value}
          initialValid={formState.inputs.description.isValid}
        />
        <Button type="submit" disabled={!formState.isValid}>UPDATE TASK</Button>
    </form>
  );
};

export default UpdateTask;
