import React, { useEffect } from "react";
import { useParams } from "react-router-dom";

import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import Card from "../../shared/components/UIElements/Card";
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH } from "../../shared/util/validators";
import { useForm } from "../../shared/hooks/form-hook";

import "./TaskForm.css";
import { useHttpClient } from "../../shared/hooks/http-hook";
import LoadingSpinner from "../../shared/components/UIElements/LoadingSpinner";
import ErrorModal from "../../shared/components/UIElements/ErrorModal";

const DUMMY_TASKS = [
    {
        taskId : 't1',
        creator : 'Ananya Gupta',
        taskDesc : 'Study JavaScript',
        creatorId: 'c1'
    },
    {
        taskId : 't2',
        creator : 'Dr. Mishra',
        taskDesc : 'Travel whole world',
        creatorId: 'c2'
    }
];

const UpdateTask = () => {
  const { isLoading, error, sendRequest, clearError} = useHttpClient();
  
  const taskId = useParams().taskId;

  const [formState, inputHandler, setFormData] = useForm({
    creator: {
        value: '',
        isValid: false
    },
    taskDesc: {
        value: '',
        isValid: false
    }
  }, false);

  useEffect(() => {
    const fetchPlace = async () => {
      try {
        const responseData = await sendRequest(
          `http://localhost:5000/api/tasks/update/${taskId}`
        );
        setFormData({
          creator: {
              value: responseData.task.creator,
              isValid: true
          },
          taskDesc: {
              value: responseData.task.taskDesc,
              isValid: true
          }
        }, true);
      } catch (err) {}    
    };
    fetchPlace();
  }, [sendRequest, taskId, setFormData]);

  const taskUpdateSubmitHandler = event => {
    event.preventDefault();
    console.log(formState.inputs);
  };

  if(!identifiedTask) {
    return (
        <div className = "center">
          <Card>
            <h2>Task Not Found</h2>
          </Card>
        </div>  
    );
  }

  if (isLoading) {
    return (
        <div className="center">
            <LoadingSpinner />
        </div>
    );
  }

  return (
   <React.Fragment>
    <ErrorModal onClear = {clearError} />
    <form className="task-form" onSubmit={taskUpdateSubmitHandler}>
        <Input 
          id="creator" 
          element="input" 
          type="text" 
          label="Creator" 
          validators={[VALIDATOR_REQUIRE()]}
          errorText="Please Enter a Valid Creator Name"
          onInput= {inputHandler}
          initialValue={formState.inputs.creator.value}
          initialValid={formState.inputs.creator.isValid}
        />
         <Input 
          id="taskDesc" 
          element="textarea"  
          label="Task" 
          validators={[VALIDATOR_MINLENGTH(5)]}
          errorText="Please Enter a Valid Task (At Least 5 Characters)"
          onInput= {inputHandler}
          initialValue={formState.inputs.taskDesc.value}
          initialValid={formState.inputs.taskDesc.isValid}
        />
        <Button type="submit" disabled={!formState.isValid}>UPDATE TASK</Button>
    </form>
   </React.Fragment>  
  );
};

export default UpdateTask;
