import React from "react";

import Input from "../../shared/components/FormElements/Input";
import Button from "../../shared/components/FormElements/Button";
import { useForm } from "../../shared/hooks/form-hook";
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH } from "../../shared/util/validators";
import "./TaskForm.css";
import { useHttpClient } from "../../shared/hooks/http-hook";
import ErrorModal from "../../shared/components/UIElements/ErrorModal";
import LoadingSpinner from "../../shared/components/UIElements/LoadingSpinner";

  const NewTask = () => {
    const {isLoading, error, sendRequest, clearError} = useHttpClient();
    const [formState, inputHandler] = useForm(
        {
            creator: {
              value: '',
              isValid: false
            },
            taskDesc: {
              value: '',
              isValid: false
            }
        }, false
     );

    const taskSubmitHandler = async event => {
        event.preventDefault();
        try {
          await sendRequest(
            'http://localhost:5000/api/tasks/create/new', 
            'POST', 
            JSON.stringify({
              creator: formState.inputs.creator.value,
              taskDesc: formState.inputs.taskDesc.value
          }), { 'Content-Type' : 'application/json' }
         );
         console.log
        } catch (err) {}    
    };
  
    return (
     <React.Fragment>
      <ErrorModal error = {error} onClear = {clearError} />
      <form className="task-form" onSubmit={taskSubmitHandler}>
        {isLoading && <LoadingSpinner asOverlay />}
        <Input
          id="creator"
          element="input"
          type="text"
          label="Creator"
          validators={[VALIDATOR_REQUIRE()]}
          errorText="Please Enter a Valid Creator Name."
          onInput={inputHandler}
        />
        <Input
          id="taskDesc"
          element="textarea"
          label="Task"
          validators={[VALIDATOR_MINLENGTH(5)]}
          errorText="Please Enter a Valid Task (At Least 5 Characters)"
          onInput={inputHandler}
        />
        <Button type="submit" disabled={!formState.isValid}>
          CREATE TASK
        </Button>
      </form>
     </React.Fragment>  
    );
  };

export default NewTask;
